contracts/core/DisputeResolution.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IDisputeResolution.sol";
import "../interfaces/IFreelanceMarketplace.sol";
import "../interfaces/IEscrow.sol";

/**
 * @title DisputeResolution
 * @dev Contract for handling disputes between clients and freelancers
 */
contract DisputeResolution is IDisputeResolution {
    // Contract owner (arbitrator)
    address private _owner;

    // Counter for dispute IDs
    struct Counter {
        uint256 _value;
    }
    Counter private _disputeIds;

    // Contract references
    IFreelanceMarketplace private _marketplace;
    IEscrow private _escrow;

    // Mappings
    mapping(uint256 => Dispute) private _disputes;
    mapping(uint256 => uint256[]) private _projectDisputes;
    mapping(address => uint256[]) private _userInitiatedDisputes;
    mapping(address => uint256[]) private _userDisputes;
    mapping(uint256 => mapping(uint256 => uint256)) private _milestoneDisputes;

    // Events
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Modifier to ensure only the owner can call certain functions
    modifier onlyOwner() {
        require(msg.sender == _owner, "Only owner can call this function");
        _;
    }

    /**
     * @dev Function to increment counter safely
     */
    function _increment(Counter storage counter) private {
        counter._value += 1;
    }

    /**
     * @dev Function to get current counter value
     */
    function _current(Counter storage counter) private view returns (uint256) {
        return counter._value;
    }

    /**
     * @dev Constructor for the DisputeResolution contract
     * @param initialOwner The initial owner of the contract (arbitrator)
     * @param marketplaceAddress The address of the marketplace contract
     * @param escrowAddress The address of the escrow contract
     */
    constructor(
        address initialOwner,
        address marketplaceAddress,
        address escrowAddress
    ) {
        require(initialOwner != address(0), "Invalid owner address");
        require(marketplaceAddress != address(0), "Invalid marketplace address");
        require(escrowAddress != address(0), "Invalid escrow address");

        _owner = initialOwner;
        _marketplace = IFreelanceMarketplace(marketplaceAddress);
        _escrow = IEscrow(escrowAddress);
    }

    /**
     * @dev Transfers ownership of the contract to a new account
     * @param newOwner The address of the new owner
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    /**
     * @dev Returns the address of the current owner
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Creates a new dispute
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     * @param respondent The address of the respondent
     * @param reason The reason for the dispute
     * @return The ID of the newly created dispute
     */
    function createDispute(
        uint256 projectId,
        uint256 milestoneId,
        address respondent,
        string calldata reason
    ) external override returns (uint256) {
        require(respondent != address(0), "Invalid respondent address");
        require(respondent != msg.sender, "Cannot dispute yourself");
        require(bytes(reason).length > 0, "Reason cannot be empty");
        require(!hasDispute(projectId, milestoneId), "Dispute already exists for this milestone");

        // Verify project and milestone
        IFreelanceMarketplace.Project memory project = _marketplace.getProject(projectId);
        require(project.id == projectId, "Project does not exist");

        // Verify sender is either client or freelancer
        require(
            msg.sender == project.client || msg.sender == project.freelancer,
            "Only project participants can create disputes"
        );

        // Verify respondent is either client or freelancer
        require(
            respondent == project.client || respondent == project.freelancer,
            "Respondent must be a project participant"
        );

        _increment(_disputeIds);
        uint256 disputeId = _current(_disputeIds);

        Dispute storage dispute = _disputes[disputeId];
        dispute.id = disputeId;
        dispute.projectId = projectId;
        dispute.milestoneId = milestoneId;
        dispute.initiator = msg.sender;
        dispute.respondent = respondent;
        dispute.reason = reason;
        dispute.status = DisputeStatus.Created;
        dispute.resolution = ResolutionType.None;
        dispute.createdAt = block.timestamp;

        // Update mappings
        _projectDisputes[projectId].push(disputeId);
        _userInitiatedDisputes[msg.sender].push(disputeId);
        _userDisputes[msg.sender].push(disputeId);
        _userDisputes[respondent].push(disputeId);
        _milestoneDisputes[projectId][milestoneId] = disputeId;

        emit DisputeCreated(disputeId, projectId, milestoneId, msg.sender, respondent);

        return disputeId;
    }

    /**
     * @dev Submits evidence for a dispute
     * @param disputeId The ID of the dispute
     * @param evidence The evidence text
     */
    function submitEvidence(uint256 disputeId, string calldata evidence) external override {
        Dispute storage dispute = _disputes[disputeId];
        require(dispute.id == disputeId, "Dispute does not exist");
        require(
            msg.sender == dispute.initiator || msg.sender == dispute.respondent,
            "Only dispute participants can submit evidence"
        );
        require(dispute.status == DisputeStatus.Created || dispute.status == DisputeStatus.InReview, "Dispute cannot accept evidence");
        require(bytes(evidence).length > 0, "Evidence cannot be empty");

        dispute.evidence = evidence;

        if (dispute.status == DisputeStatus.Created) {
            dispute.status = DisputeStatus.InReview;
        }

        emit EvidenceSubmitted(disputeId, msg.sender, evidence);
    }

    /**
     * @dev Submits a response to a dispute
     * @param disputeId The ID of the dispute
     * @param response The response text
     */
    function submitResponse(uint256 disputeId, string calldata response) external override {
        Dispute storage dispute = _disputes[disputeId];
        require(dispute.id == disputeId, "Dispute does not exist");
        require(msg.sender == dispute.respondent, "Only respondent can submit response");
        require(dispute.status == DisputeStatus.Created || dispute.status == DisputeStatus.InReview, "Dispute cannot accept responses");
        require(bytes(response).length > 0, "Response cannot be empty");

        dispute.response = response;

        if (dispute.status == DisputeStatus.Created) {
            dispute.status = DisputeStatus.InReview;
        }

        emit ResponseSubmitted(disputeId, msg.sender, response);
    }

    /**
     * @dev Resolves a dispute
     * @param disputeId The ID of the dispute
     * @param resolution The resolution type
     */
    function resolveDispute(
        uint256 disputeId,
        ResolutionType resolution
    ) external override onlyOwner {
        Dispute storage dispute = _disputes[disputeId];
        require(dispute.id == disputeId, "Dispute does not exist");
        require(dispute.status == DisputeStatus.InReview, "Dispute is not in review");
        require(resolution != ResolutionType.None, "Invalid resolution type");

        // Get the escrow ID
        uint256 escrowId = _escrow.getMilestoneEscrow(dispute.projectId, dispute.milestoneId);

        // Get the escrow details
        IEscrow.EscrowPayment memory escrowPayment = _escrow.getEscrow(escrowId);

        // Apply resolution
        if (resolution == ResolutionType.ClientFavor) {
            // Refund to client
            _escrow.refundEscrow(escrowId);
        } else if (resolution == ResolutionType.FreelancerFavor) {
            // Release to freelancer
            _escrow.releaseEscrow(escrowId);
        } else if (resolution == ResolutionType.Split) {
            // Split the funds
            uint256 amount = escrowPayment.amount / 2;
            address payable freelancer = payable(escrowPayment.freelancer);
            _escrow.resolveDispute(escrowId, freelancer, amount);
        }

        // Update dispute status
        dispute.status = DisputeStatus.Resolved;
        dispute.resolution = resolution;
        dispute.resolvedAt = block.timestamp;

        emit DisputeResolved(disputeId, dispute.projectId, resolution);
    }

    /**
     * @dev Cancels a dispute
     * @param disputeId The ID of the dispute
     */
    function cancelDispute(uint256 disputeId) external override {
        Dispute storage dispute = _disputes[disputeId];
        require(dispute.id == disputeId, "Dispute does not exist");
        require(dispute.status != DisputeStatus.Resolved && dispute.status != DisputeStatus.Cancelled, "Dispute already resolved or cancelled");
        require(
            msg.sender == dispute.initiator || msg.sender == _owner,
            "Only initiator or arbitrator can cancel"
        );

        dispute.status = DisputeStatus.Cancelled;

        emit DisputeResolved(disputeId, dispute.projectId, ResolutionType.None);
    }

    /**
     * @dev Gets a dispute by ID
     * @param disputeId The ID of the dispute
     * @return The dispute details
     */
    function getDispute(uint256 disputeId) external view override returns (Dispute memory) {
        require(_disputes[disputeId].id == disputeId, "Dispute does not exist");
        return _disputes[disputeId];
    }

    /**
     * @dev Gets all disputes for a project
     * @param projectId The ID of the project
     * @return An array of dispute IDs
     */
    function getProjectDisputes(uint256 projectId) external view override returns (uint256[] memory) {
        return _projectDisputes[projectId];
    }

    /**
     * @dev Gets all disputes initiated by a user
     * @param user The address of the user
     * @return An array of dispute IDs
     */
    function getUserInitiatedDisputes(address user) external view override returns (uint256[] memory) {
        return _userInitiatedDisputes[user];
    }

    /**
     * @dev Gets all disputes involving a user (either as initiator or respondent)
     * @param user The address of the user
     * @return An array of dispute IDs
     */
    function getUserDisputes(address user) external view override returns (uint256[] memory) {
        return _userDisputes[user];
    }

    /**
     * @dev Checks if a dispute exists for a milestone
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     * @return Whether a dispute exists
     */
    function hasDispute(uint256 projectId, uint256 milestoneId) public view override returns (bool) {
        return _milestoneDisputes[projectId][milestoneId] != 0;
    }
}

contracts/core/Escrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IEscrow.sol";
import "../interfaces/IFreelanceMarketplace.sol";

/**
 * @title Escrow
 * @dev Contract for handling secure payments between clients and freelancers
 */
contract Escrow is IEscrow {
    // Contract owner (arbitrator)
    address private _owner;

    // Reentrancy guard
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;
    uint256 private _status;

    // Contract counter
    struct Counter {
        uint256 _value;
    }
    Counter private _escrowIds;

    // Protocol fee percentage (in basis points, e.g., 250 = 2.5%)
    uint256 private _protocolFeeRate;

    // Treasury address to receive protocol fees
    address private _treasury;

    // Marketplace contract reference
    IFreelanceMarketplace private _marketplace;

    // Mappings
    mapping(uint256 => EscrowPayment) private _escrows;
    mapping(uint256 => uint256[]) private _projectEscrows;
    mapping(uint256 => mapping(uint256 => uint256)) private _milestoneEscrows;

    // Events
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Modifier to ensure only the marketplace contract can call certain functions
    modifier onlyMarketplace() {
        require(msg.sender == address(_marketplace), "Only marketplace can call this function");
        _;
    }

    // Modifier to ensure only an arbitrator can call certain functions
    modifier onlyArbitrator() {
        require(msg.sender == _owner, "Only arbitrator can call this function");
        _;
    }

    // Modifier to ensure only the owner can call certain functions
    modifier onlyOwner() {
        require(msg.sender == _owner, "Only owner can call this function");
        _;
    }

    // Modifier to prevent reentrancy attacks
    modifier nonReentrant() {
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");
        _status = _ENTERED;
        _;
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Constructor for the Escrow contract
     * @param initialOwner The initial owner of the contract (arbitrator)
     * @param marketplaceAddress The address of the Freelance Marketplace contract
     * @param treasuryAddress The address of the treasury to receive protocol fees
     * @param protocolFeeRate The protocol fee rate in basis points (e.g., 250 = 2.5%)
     */
    constructor(
        address initialOwner,
        address marketplaceAddress,
        address treasuryAddress,
        uint256 protocolFeeRate
    ) {
        require(initialOwner != address(0), "Invalid owner address");
        require(marketplaceAddress != address(0), "Invalid marketplace address");
        require(treasuryAddress != address(0), "Invalid treasury address");
        require(protocolFeeRate <= 1000, "Fee cannot exceed 10%");

        _owner = initialOwner;
        _marketplace = IFreelanceMarketplace(marketplaceAddress);
        _treasury = treasuryAddress;
        _protocolFeeRate = protocolFeeRate;
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Transfers ownership of the contract to a new account
     * @param newOwner The address of the new owner
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    /**
     * @dev Returns the address of the current owner
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Function to increment counter safely
     */
    function _increment(Counter storage counter) private {
        counter._value += 1;
    }

    /**
     * @dev Function to get current counter value
     */
    function _current(Counter storage counter) private view returns (uint256) {
        return counter._value;
    }

    /**
     * @dev Sets the marketplace contract address
     * @param marketplaceAddress The address of the Freelance Marketplace contract
     */
    function setMarketplace(address marketplaceAddress) external onlyOwner {
        require(marketplaceAddress != address(0), "Invalid marketplace address");
        _marketplace = IFreelanceMarketplace(marketplaceAddress);
    }

    /**
     * @dev Sets the treasury address
     * @param treasuryAddress The address of the treasury to receive protocol fees
     */
    function setTreasury(address treasuryAddress) external onlyOwner {
        require(treasuryAddress != address(0), "Invalid treasury address");
        _treasury = treasuryAddress;
    }

    /**
     * @dev Sets the protocol fee rate
     * @param protocolFeeRate The protocol fee rate in basis points (e.g., 250 = 2.5%)
     */
    function setProtocolFeeRate(uint256 protocolFeeRate) external onlyOwner {
        require(protocolFeeRate <= 1000, "Fee cannot exceed 10%");
        _protocolFeeRate = protocolFeeRate;
    }

    /**
     * @dev Creates a new escrow payment
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     * @param client The address of the client
     * @param freelancer The address of the freelancer
     * @return The ID of the newly created escrow
     */
    function createEscrow(
        uint256 projectId,
        uint256 milestoneId,
        address client,
        address freelancer
    ) external payable override nonReentrant returns (uint256) {
        require(msg.value > 0, "Amount must be greater than 0");
        require(client != address(0), "Invalid client address");
        require(freelancer != address(0), "Invalid freelancer address");
        require(client != freelancer, "Client and freelancer cannot be the same");

        _increment(_escrowIds);
        uint256 escrowId = _current(_escrowIds);

        EscrowPayment storage escrow = _escrows[escrowId];
        escrow.id = escrowId;
        escrow.projectId = projectId;
        escrow.milestoneId = milestoneId;
        escrow.client = client;
        escrow.freelancer = freelancer;
        escrow.amount = msg.value;
        escrow.status = EscrowStatus.Funded;
        escrow.createdAt = block.timestamp;

        _projectEscrows[projectId].push(escrowId);
        _milestoneEscrows[projectId][milestoneId] = escrowId;

        emit EscrowCreated(
            escrowId,
            projectId,
            milestoneId,
            client,
            freelancer,
            msg.value
        );

        return escrowId;
    }

    /**
     * @dev Releases the escrow payment to the freelancer
     * @param escrowId The ID of the escrow
     */
    function releaseEscrow(uint256 escrowId) external override nonReentrant {
        EscrowPayment storage escrow = _escrows[escrowId];
        require(escrow.id == escrowId, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Funded, "Escrow is not in funded state");

        // Only client or marketplace can release funds
        IFreelanceMarketplace.Project memory project = _marketplace.getProject(escrow.projectId);
        require(
            msg.sender == escrow.client || 
            msg.sender == address(_marketplace) || 
            msg.sender == owner(),
            "Only client, marketplace, or arbitrator can release funds"
        );

        escrow.status = EscrowStatus.Released;

        // Calculate protocol fee
        uint256 fee = (escrow.amount * _protocolFeeRate) / 10000;
        uint256 freelancerAmount = escrow.amount - fee;

        // Transfer funds to freelancer and treasury
        (bool freelancerSuccess, ) = payable(escrow.freelancer).call{value: freelancerAmount}("");
        require(freelancerSuccess, "Transfer to freelancer failed");

        if (fee > 0) {
            (bool treasurySuccess, ) = payable(_treasury).call{value: fee}("");
            require(treasurySuccess, "Transfer to treasury failed");
        }

        emit EscrowReleased(
            escrowId,
            escrow.projectId,
            escrow.milestoneId,
            escrow.freelancer,
            freelancerAmount
        );
    }

    /**
     * @dev Refunds the escrow payment to the client
     * @param escrowId The ID of the escrow
     */
    function refundEscrow(uint256 escrowId) external override nonReentrant {
        EscrowPayment storage escrow = _escrows[escrowId];
        require(escrow.id == escrowId, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Funded, "Escrow is not in funded state");

        // Only freelancer, marketplace or arbitrator can refund
        require(
            msg.sender == escrow.freelancer || 
            msg.sender == address(_marketplace) || 
            msg.sender == owner(),
            "Only freelancer, marketplace, or arbitrator can refund"
        );

        escrow.status = EscrowStatus.Refunded;

        // Transfer funds back to client
        (bool success, ) = payable(escrow.client).call{value: escrow.amount}("");
        require(success, "Transfer to client failed");

        emit EscrowRefunded(
            escrowId,
            escrow.projectId,
            escrow.milestoneId,
            escrow.client,
            escrow.amount
        );
    }

    /**
     * @dev Disputes an escrow payment
     * @param escrowId The ID of the escrow
     */
    function disputeEscrow(uint256 escrowId) external override nonReentrant {
        EscrowPayment storage escrow = _escrows[escrowId];
        require(escrow.id == escrowId, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Funded, "Escrow is not in funded state");
        require(
            msg.sender == escrow.client || 
            msg.sender == escrow.freelancer || 
            msg.sender == address(_marketplace),
            "Only client, freelancer, or marketplace can dispute"
        );

        escrow.status = EscrowStatus.Disputed;

        emit EscrowDisputed(
            escrowId,
            escrow.projectId,
            escrow.milestoneId,
            msg.sender
        );
    }

    /**
     * @dev Resolves a disputed escrow payment
     * @param escrowId The ID of the escrow
     * @param recipient The address to receive the funds
     * @param amount The amount to send to the recipient
     */
    function resolveDispute(
        uint256 escrowId,
        address payable recipient,
        uint256 amount
    ) external override onlyArbitrator nonReentrant {
        EscrowPayment storage escrow = _escrows[escrowId];
        require(escrow.id == escrowId, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Disputed, "Escrow is not in disputed state");
        require(amount <= escrow.amount, "Amount exceeds escrow amount");
        require(
            recipient == escrow.client || 
            recipient == escrow.freelancer,
            "Recipient must be client or freelancer"
        );

        // Calculate remaining amount after resolving to the specified recipient
        uint256 remaining = escrow.amount - amount;

        // Determine the other party
        address payable otherParty;
        if (recipient == escrow.client) {
            otherParty = payable(escrow.freelancer);
        } else {
            otherParty = payable(escrow.client);
        }

        // Mark escrow as released to prevent re-entrancy
        escrow.status = EscrowStatus.Released;

        // Transfer the specified amount to the recipient
        if (amount > 0) {
            (bool successRecipient, ) = recipient.call{value: amount}("");
            require(successRecipient, "Transfer to recipient failed");
        }

        // Transfer any remaining amount to the other party
        if (remaining > 0) {
            (bool successOther, ) = otherParty.call{value: remaining}("");
            require(successOther, "Transfer to other party failed");
        }

        // Emit appropriate event based on who received funds
        if (recipient == escrow.freelancer) {
            emit EscrowReleased(
                escrowId,
                escrow.projectId,
                escrow.milestoneId,
                escrow.freelancer,
                amount
            );
        } else {
            emit EscrowRefunded(
                escrowId,
                escrow.projectId,
                escrow.milestoneId,
                escrow.client,
                amount
            );
        }
    }

    /**
     * @dev Gets an escrow payment by ID
     * @param escrowId The ID of the escrow
     * @return The escrow payment details
     */
    function getEscrow(uint256 escrowId) external view override returns (EscrowPayment memory) {
        require(_escrows[escrowId].id == escrowId, "Escrow does not exist");
        return _escrows[escrowId];
    }

    /**
     * @dev Gets all escrow payments for a project
     * @param projectId The ID of the project
     * @return An array of escrow IDs
     */
    function getProjectEscrows(uint256 projectId) external view override returns (uint256[] memory) {
        return _projectEscrows[projectId];
    }

    /**
     * @dev Gets the escrow payment for a milestone
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     * @return The ID of the escrow
     */
    function getMilestoneEscrow(uint256 projectId, uint256 milestoneId) external view override returns (uint256) {
        return _milestoneEscrows[projectId][milestoneId];
    }

    /**
     * @dev Gets the total amount held in escrow for a project
     * @param projectId The ID of the project
     * @return The total amount held in escrow
     */
    function getProjectEscrowBalance(uint256 projectId) external view override returns (uint256) {
        uint256[] memory escrowIds = _projectEscrows[projectId];
        uint256 totalBalance = 0;

        for (uint256 i = 0; i < escrowIds.length; i++) {
            EscrowPayment storage escrow = _escrows[escrowIds[i]];
            if (escrow.status == EscrowStatus.Funded) {
                totalBalance += escrow.amount;
            }
        }

        return totalBalance;
    }

    /**
     * @dev Gets the protocol fee rate
     * @return The protocol fee rate in basis points
     */
    function getProtocolFeeRate() external view returns (uint256) {
        return _protocolFeeRate;
    }

    /**
     * @dev Gets the treasury address
     * @return The address of the treasury
     */
    function getTreasury() external view returns (address) {
        return _treasury;
    }

    /**
     * @dev Gets the marketplace contract address
     * @return The address of the marketplace contract
     */
    function getMarketplace() external view returns (address) {
        return address(_marketplace);
    }
}

contracts/core/FreelanceMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IFreelanceMarketplace.sol";
import "../interfaces/IEscrow.sol";
import "../interfaces/IReputationSystem.sol";
import "../interfaces/IDisputeResolution.sol";

/**
 * @title FreelanceMarketplace
 * @dev Main contract for the decentralized freelance marketplace
 */
contract FreelanceMarketplace is IFreelanceMarketplace {
    // Contract owner
    address private _owner;

    // Reentrancy guard
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;
    uint256 private _status;

    // Contract counters
    struct Counter {
        uint256 _value;
    }
    Counter private _projectIds;
    Counter private _milestoneIds;
    Counter private _proposalIds;

    // Associated contracts
    IEscrow private _escrowContract;
    IReputationSystem private _reputationSystem;
    IDisputeResolution private _disputeResolution;

    // Protocol fee percentage (in basis points, e.g., 250 = 2.5%)
    uint256 private _protocolFeeRate = 250;

    // Mappings
    mapping(uint256 => Project) private _projects;
    mapping(uint256 => Milestone) private _milestones;
    mapping(uint256 => Proposal) private _proposals;
    mapping(address => uint256[]) private _clientProjects;
    mapping(address => uint256[]) private _freelancerProjects;
    mapping(uint256 => uint256[]) private _projectProposals;

    // Events
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Modifier to ensure only the owner can call certain functions
    modifier onlyOwner() {
        require(msg.sender == _owner, "Only owner can call this function");
        _;
    }

    // Modifier to prevent reentrancy attacks
    modifier nonReentrant() {
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");
        _status = _ENTERED;
        _;
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Function to increment counter safely
     */
    function _increment(Counter storage counter) private {
        counter._value += 1;
    }

    /**
     * @dev Function to get current counter value
     */
    function _current(Counter storage counter) private view returns (uint256) {
        return counter._value;
    }

    /**
     * @dev Constructor for the FreelanceMarketplace contract
     * @param initialOwner The initial owner of the contract
     */
    constructor(address initialOwner) {
        require(initialOwner != address(0), "Invalid owner address");
        _owner = initialOwner;
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Transfers ownership of the contract to a new account
     * @param newOwner The address of the new owner
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    /**
     * @dev Returns the address of the current owner
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Sets the escrow contract address
     * @param escrowAddress The address of the escrow contract
     */
    function setEscrowContract(address escrowAddress) external onlyOwner {
        _escrowContract = IEscrow(escrowAddress);
    }

    /**
     * @dev Sets the reputation system contract address
     * @param reputationAddress The address of the reputation system contract
     */
    function setReputationSystem(address reputationAddress) external onlyOwner {
        _reputationSystem = IReputationSystem(reputationAddress);
    }

    /**
     * @dev Sets the dispute resolution contract address
     * @param disputeResolutionAddress The address of the dispute resolution contract
     */
    function setDisputeResolution(address disputeResolutionAddress) external onlyOwner {
        _disputeResolution = IDisputeResolution(disputeResolutionAddress);
    }

    /**
     * @dev Sets the protocol fee rate
     * @param feeRate The protocol fee rate in basis points (e.g., 250 = 2.5%)
     */
    function setProtocolFeeRate(uint256 feeRate) external onlyOwner {
        require(feeRate <= 1000, "Fee cannot exceed 10%");
        _protocolFeeRate = feeRate;
    }

    /**
     * @dev Creates a new project
     * @param title The title of the project
     * @param description The description of the project
     * @param totalBudget The total budget for the project
     * @param deadline The deadline for the project completion
     * @return The ID of the newly created project
     */
    function createProject(
        string calldata title,
        string calldata description,
        uint256 totalBudget,
        uint256 deadline
    ) external payable override nonReentrant returns (uint256) {
        require(bytes(title).length > 0, "Title cannot be empty");
        require(bytes(description).length > 0, "Description cannot be empty");
        require(totalBudget > 0, "Budget must be greater than 0");
        require(deadline > block.timestamp, "Deadline must be in the future");
        require(msg.value == totalBudget, "Sent value must match the budget");

        _increment(_projectIds);
        uint256 projectId = _current(_projectIds);

        Project storage project = _projects[projectId];
        project.id = projectId;
        project.client = msg.sender;
        project.title = title;
        project.description = description;
        project.totalBudget = totalBudget;
        project.createdAt = block.timestamp;
        project.deadline = deadline;
        project.status = ProjectStatus.Open;

        _clientProjects[msg.sender].push(projectId);

        emit ProjectCreated(projectId, msg.sender, title, totalBudget);

        return projectId;
    }

    /**
     * @dev Adds a milestone to a project
     * @param projectId The ID of the project
     * @param description The description of the milestone
     * @param amount The amount allocated for the milestone
     * @param deadline The deadline for the milestone completion
     * @return The ID of the newly created milestone
     */
    function addMilestone(
        uint256 projectId,
        string calldata description,
        uint256 amount,
        uint256 deadline
    ) external override nonReentrant returns (uint256) {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(msg.sender == project.client, "Only client can add milestones");
        require(project.status == ProjectStatus.Open || project.status == ProjectStatus.InProgress, "Project must be open or in progress");
        require(bytes(description).length > 0, "Description cannot be empty");
        require(amount > 0, "Amount must be greater than 0");
        require(deadline > block.timestamp, "Deadline must be in the future");
        require(deadline <= project.deadline, "Milestone deadline cannot exceed project deadline");

        uint256 totalMilestoneAmount = 0;
        for (uint256 i = 0; i < project.milestoneIds.length; i++) {
            totalMilestoneAmount += _milestones[project.milestoneIds[i]].amount;
        }

        require(totalMilestoneAmount + amount <= project.totalBudget, "Total milestone amounts exceed project budget");

        _increment(_milestoneIds);
        uint256 milestoneId = _current(_milestoneIds);

        Milestone storage milestone = _milestones[milestoneId];
        milestone.id = milestoneId;
        milestone.projectId = projectId;
        milestone.description = description;
        milestone.amount = amount;
        milestone.deadline = deadline;
        milestone.status = MilestoneStatus.Pending;

        project.milestoneIds.push(milestoneId);

        emit MilestoneCreated(projectId, milestoneId, amount);

        return milestoneId;
    }

    /**
     * @dev Submits a proposal for a project
     * @param projectId The ID of the project
     * @param description The description of the proposal
     * @param estimatedCompletionTime The estimated completion time in seconds
     * @param proposedBudget The proposed budget for the project
     * @return The ID of the newly created proposal
     */
    function submitProposal(
        uint256 projectId,
        string calldata description,
        uint256 estimatedCompletionTime,
        uint256 proposedBudget
    ) external override nonReentrant returns (uint256) {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(project.status == ProjectStatus.Open, "Project must be open for proposals");
        require(msg.sender != project.client, "Client cannot submit proposals");
        require(bytes(description).length > 0, "Description cannot be empty");
        require(estimatedCompletionTime > 0, "Estimated completion time must be greater than 0");
        require(proposedBudget > 0, "Proposed budget must be greater than 0");

        for (uint256 i = 0; i < _projectProposals[projectId].length; i++) {
            Proposal storage existingProposal = _proposals[_projectProposals[projectId][i]];
            require(existingProposal.freelancer != msg.sender, "Freelancer already submitted a proposal");
        }

        _increment(_proposalIds);
        uint256 proposalId = _current(_proposalIds);

        Proposal storage proposal = _proposals[proposalId];
        proposal.id = proposalId;
        proposal.projectId = projectId;
        proposal.freelancer = msg.sender;
        proposal.description = description;
        proposal.estimatedCompletionTime = estimatedCompletionTime;
        proposal.proposedBudget = proposedBudget;
        proposal.accepted = false;

        _projectProposals[projectId].push(proposalId);

        emit ProposalSubmitted(projectId, proposalId, msg.sender);

        return proposalId;
    }

    /**
     * @dev Accepts a proposal for a project
     * @param projectId The ID of the project
     * @param proposalId The ID of the proposal
     */
    function acceptProposal(uint256 projectId, uint256 proposalId) external override nonReentrant {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(msg.sender == project.client, "Only client can accept proposals");
        require(project.status == ProjectStatus.Open, "Project must be open for proposals");

        Proposal storage proposal = _proposals[proposalId];
        require(proposal.id == proposalId, "Proposal does not exist");
        require(proposal.projectId == projectId, "Proposal is not for this project");
        require(!proposal.accepted, "Proposal already accepted");

        project.freelancer = proposal.freelancer;
        project.status = ProjectStatus.InProgress;

        proposal.accepted = true;

        _freelancerProjects[proposal.freelancer].push(projectId);

        emit ProposalAccepted(projectId, proposalId, proposal.freelancer);
        emit ProjectStatusUpdated(projectId, ProjectStatus.InProgress);
    }

    /**
     * @dev Starts a milestone
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     */
    function startMilestone(uint256 projectId, uint256 milestoneId) external override nonReentrant {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(msg.sender == project.freelancer, "Only freelancer can start milestones");
        require(project.status == ProjectStatus.InProgress, "Project must be in progress");

        Milestone storage milestone = _milestones[milestoneId];
        require(milestone.id == milestoneId, "Milestone does not exist");
        require(milestone.projectId == projectId, "Milestone is not for this project");
        require(milestone.status == MilestoneStatus.Pending, "Milestone must be pending");

        milestone.status = MilestoneStatus.InProgress;

        // Create escrow for this milestone
        _escrowContract.createEscrow{value: milestone.amount}(
            projectId,
            milestoneId,
            project.client,
            project.freelancer
        );

        emit MilestoneStatusUpdated(milestoneId, MilestoneStatus.InProgress);
    }

    /**
     * @dev Completes a milestone
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     */
    function completeMilestone(uint256 projectId, uint256 milestoneId) external override nonReentrant {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(msg.sender == project.freelancer, "Only freelancer can complete milestones");
        require(project.status == ProjectStatus.InProgress, "Project must be in progress");

        Milestone storage milestone = _milestones[milestoneId];
        require(milestone.id == milestoneId, "Milestone does not exist");
        require(milestone.projectId == projectId, "Milestone is not for this project");
        require(milestone.status == MilestoneStatus.InProgress, "Milestone must be in progress");

        milestone.status = MilestoneStatus.Completed;

        emit MilestoneStatusUpdated(milestoneId, MilestoneStatus.Completed);
    }

    /**
     * @dev Approves a completed milestone and releases funds
     * @param projectId The ID of the project
     * @param milestoneId The ID of the milestone
     */
    function approveMilestone(uint256 projectId, uint256 milestoneId) external override nonReentrant {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(msg.sender == project.client, "Only client can approve milestones");
        require(project.status == ProjectStatus.InProgress, "Project must be in progress");

        Milestone storage milestone = _milestones[milestoneId];
        require(milestone.id == milestoneId, "Milestone does not exist");
        require(milestone.projectId == projectId, "Milestone is not for this project");
        require(milestone.status == MilestoneStatus.Completed, "Milestone must be completed");

        // Get the escrow ID for this milestone
        uint256 escrowId = _escrowContract.getMilestoneEscrow(projectId, milestoneId);

        // Release the escrow funds
        _escrowContract.releaseEscrow(escrowId);

        // Check if all milestones are completed
        bool allCompleted = true;
        for (uint256 i = 0; i < project.milestoneIds.length; i++) {
            if (_milestones[project.milestoneIds[i]].status != MilestoneStatus.Completed) {
                allCompleted = false;
                break;
            }
        }

        // If all milestones are completed, mark the project as completed
        if (allCompleted) {
            project.status = ProjectStatus.Completed;
            emit ProjectStatusUpdated(projectId, ProjectStatus.Completed);
        }

        emit MilestoneFundsReleased(projectId, milestoneId, project.freelancer, milestone.amount);
    }

    /**
     * @dev Creates a dispute for a project
     * @param projectId The ID of the project
     * @param reason The reason for the dispute
     */
    function createDispute(uint256 projectId, string calldata reason) external override nonReentrant {
        Project storage project = _projects[projectId];
        require(project.id == projectId, "Project does not exist");
        require(msg.sender == project.client || msg.sender == project.freelancer, "Only project participants can create disputes");
        require(project.status == ProjectStatus.InProgress, "Project must be in progress");
        require(bytes(reason).length > 0, "Reason cannot be empty");

        project.status = ProjectStatus.Disputed;

        address respondent = msg.sender == project.client ? project.freelancer : project.client;

        // Create a dispute in the dispute resolution contract
        uint256 milestoneId = 0; // Find the current milestone in progress or dispute
        for (uint256 i = 0; i < project.milestoneIds.length; i++) {
            Milestone storage milestone = _milestones[project.milestoneIds[i]];
            if (milestone.status == MilestoneStatus.InProgress || milestone.status == MilestoneStatus.Completed) {
                milestoneId = milestone.id;
                milestone.status = MilestoneStatus.Disputed;
                emit MilestoneStatusUpdated(milestoneId, MilestoneStatus.Disputed);
                break;
            }
        }

        _disputeResolution.createDispute(projectId, milestoneId, respondent, reason);

        emit DisputeCreated(projectId, msg.sender);
        emit ProjectStatusUpdated(projectId, ProjectStatus.Disputed);
    }

    /**
     * @dev Gets a project by ID
     * @param projectId The ID of the project
     * @return The project details
     */
    function getProject(uint256 projectId) external view override returns (Project memory) {
        require(_projects[projectId].id == projectId, "Project does not exist");
        return _projects[projectId];
    }

    /**
     * @dev Gets a milestone by ID
     * @param milestoneId The ID of the milestone
     * @return The milestone details
     */
    function getMilestone(uint256 milestoneId) external view override returns (Milestone memory) {
        require(_milestones[milestoneId].id == milestoneId, "Milestone does not exist");
        return _milestones[milestoneId];
    }

    /**
     * @dev Gets a proposal by ID
     * @param proposalId The ID of the proposal
     * @return The proposal details
     */
    function getProposal(uint256 proposalId) external view override returns (Proposal memory) {
        require(_proposals[proposalId].id == proposalId, "Proposal does not exist");
        return _proposals[proposalId];
    }

    /**
     * @dev Gets all projects created by a client
     * @param client The address of the client
     * @return An array of project IDs
     */
    function getClientProjects(address client) external view override returns (uint256[] memory) {
        return _clientProjects[client];
    }

    /**
     * @dev Gets all projects assigned to a freelancer
     * @param freelancer The address of the freelancer
     * @return An array of project IDs
     */
    function getFreelancerProjects(address freelancer) external view override returns (uint256[] memory) {
        return _freelancerProjects[freelancer];
    }

    /**
     * @dev Gets all proposals for a project
     * @param projectId The ID of the project
     * @return An array of proposal IDs
     */
    function getProjectProposals(uint256 projectId) external view override returns (uint256[] memory) {
        require(_projects[projectId].id == projectId, "Project does not exist");
        return _projectProposals[projectId];
    }

    /**
     * @dev Gets the protocol fee rate
     * @return The protocol fee rate in basis points
     */
    function getProtocolFeeRate() external view returns (uint256) {
        return _protocolFeeRate;
    }

    /**
     * @dev Gets the escrow contract address
     * @return The address of the escrow contract
     */
    function getEscrowContract() external view returns (address) {
        return address(_escrowContract);
    }

    /**
     * @dev Gets the reputation system contract address
     * @return The address of the reputation system contract
     */
    function getReputationSystem() external view returns (address) {
        return address(_reputationSystem);
    }

    /**
     * @dev Gets the dispute resolution contract address
     * @return The address of the dispute resolution contract
     */
    function getDisputeResolution() external view returns (address) {
        return address(_disputeResolution);
    }
}

contracts/core/ReputationSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IReputationSystem.sol";
import "../interfaces/IFreelanceMarketplace.sol";

/**
 * @title ReputationSystem
 * @dev Contract for managing user reputation and reviews
 */
contract ReputationSystem is IReputationSystem {
    // Contract owner
    address private _owner;

    // Counter for review IDs
    struct Counter {
        uint256 _value;
    }
    Counter private _reviewIds;

    // Marketplace contract reference
    IFreelanceMarketplace private _marketplace;

    // Mappings
    mapping(uint256 => Review) private _reviews;
    mapping(address => Reputation) private _reputations;
    mapping(uint256 => uint256[]) private _projectReviews;
    mapping(uint256 => mapping(address => mapping(address => bool))) private _hasReviewed;

    // Events
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Modifier to ensure only the owner can call certain functions
    modifier onlyOwner() {
        require(msg.sender == _owner, "Only owner can call this function");
        _;
    }

    /**
     * @dev Function to increment counter safely
     */
    function _increment(Counter storage counter) private {
        counter._value += 1;
    }

    /**
     * @dev Function to get current counter value
     */
    function _current(Counter storage counter) private view returns (uint256) {
        return counter._value;
    }

    /**
     * @dev Constructor for the ReputationSystem contract
     * @param initialOwner The initial owner of the contract
     * @param marketplaceAddress The address of the marketplace contract
     */
    constructor(address initialOwner, address marketplaceAddress) {
        require(initialOwner != address(0), "Invalid owner address");
        require(marketplaceAddress != address(0), "Invalid marketplace address");

        _owner = initialOwner;
        _marketplace = IFreelanceMarketplace(marketplaceAddress);
    }

    /**
     * @dev Transfers ownership of the contract to a new account
     * @param newOwner The address of the new owner
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    /**
     * @dev Returns the address of the current owner
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Submits a review for a freelancer or client
     * @param projectId The ID of the project
     * @param reviewee The address of the user being reviewed
     * @param rating The rating (1-5 stars)
     * @param comment The text comment
     * @return The ID of the newly created review
     */
    function submitReview(
        uint256 projectId,
        address reviewee,
        uint8 rating,
        string calldata comment
    ) external override returns (uint256) {
        require(rating >= 1 && rating <= 5, "Rating must be between 1 and 5");
        require(canReview(msg.sender, projectId, reviewee), "Not authorized to review");

        _increment(_reviewIds);
        uint256 reviewId = _current(_reviewIds);

        Review storage review = _reviews[reviewId];
        review.id = reviewId;
        review.projectId = projectId;
        review.reviewer = msg.sender;
        review.reviewee = reviewee;
        review.rating = rating;
        review.comment = comment;
        review.timestamp = block.timestamp;

        // Update reputation
        Reputation storage reputation = _reputations[reviewee];
        reputation.totalRating += rating;
        reputation.reviewCount += 1;
        reputation.reviewIds.push(reviewId);

        // Mark as reviewed
        _hasReviewed[projectId][msg.sender][reviewee] = true;

        // Add to project reviews
        _projectReviews[projectId].push(reviewId);

        emit ReviewSubmitted(reviewId, projectId, msg.sender, reviewee, rating);

        return reviewId;
    }

    /**
     * @dev Gets a review by ID
     * @param reviewId The ID of the review
     * @return The review details
     */
    function getReview(uint256 reviewId) external view override returns (Review memory) {
        require(_reviews[reviewId].id == reviewId, "Review does not exist");
        return _reviews[reviewId];
    }

    /**
     * @dev Gets all reviews for a user
     * @param user The address of the user
     * @return An array of review IDs
     */
    function getUserReviews(address user) external view override returns (uint256[] memory) {
        return _reputations[user].reviewIds;
    }

    /**
     * @dev Gets a user's average rating
     * @param user The address of the user
     * @return rating The average rating
     * @return reviewCount The number of reviews
     */
    function getUserRating(address user) external view override returns (uint256 rating, uint256 reviewCount) {
        Reputation storage reputation = _reputations[user];
        reviewCount = reputation.reviewCount;

        if (reviewCount > 0) {
            rating = reputation.totalRating / reviewCount;
        } else {
            rating = 0;
        }

        return (rating, reviewCount);
    }

    /**
     * @dev Gets all reviews for a project
     * @param projectId The ID of the project
     * @return An array of review IDs
     */
    function getProjectReviews(uint256 projectId) external view override returns (uint256[] memory) {
        return _projectReviews[projectId];
    }

    /**
     * @dev Checks if a user can submit a review for a project
     * @param reviewer The address of the reviewer
     * @param projectId The ID of the project
     * @param reviewee The address of the user being reviewed
     * @return Whether the user can submit a review
     */
    function canReview(address reviewer, uint256 projectId, address reviewee) public view override returns (bool) {
        // Check if already reviewed
        if (_hasReviewed[projectId][reviewer][reviewee]) {
            return false;
        }

        // Get project details from marketplace
        IFreelanceMarketplace.Project memory project = _marketplace.getProject(projectId);

        // Verify project exists and is completed
        if (project.id != projectId || project.status != IFreelanceMarketplace.ProjectStatus.Completed) {
            return false;
        }

        // Verify reviewer is either client or freelancer
        if (reviewer != project.client && reviewer != project.freelancer) {
            return false;
        }

        // Verify reviewee is either client or freelancer and not the reviewer
        if ((reviewee != project.client && reviewee != project.freelancer) || reviewee == reviewer) {
            return false;
        }

        return true;
    }

    /**
     * @dev Gets the reputation details for a user
     * @param user The address of the user
     * @return The reputation details
     */
    function getReputation(address user) external view override returns (Reputation memory) {
        return _reputations[user];
    }
}

contracts/interfaces/IDisputeResolution..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IDisputeResolution
 * @dev Interface for the Dispute Resolution contract
 */
interface IDisputeResolution {
    /**
     * @dev Enum representing the status of a dispute
     */
    enum DisputeStatus {
        Created,
        InReview,
        Resolved,
        Cancelled
    }

    /**
     * @dev Enum representing the resolution type
     */
    enum ResolutionType {
        None,
        ClientFavor,
        FreelancerFavor,
        Split
    }

    /**
     * @dev Struct representing a dispute
     */
    struct Dispute {
        uint256 id;
        uint256 projectId;
        uint256 milestoneId;
        address initiator;
        address respondent;
        string reason;
        string evidence;
        string response;
        DisputeStatus status;
        ResolutionType resolution;
        uint256 createdAt;
        uint256 resolvedAt;
    }

    /**
     * @dev Emitted when a new dispute is created
     */
    event DisputeCreated(
        uint256 indexed disputeId,
        uint256 indexed projectId,
        uint256 indexed milestoneId,
        address initiator,
        address respondent
    );

    /**
     * @dev Emitted when a dispute is resolved
     */
    event DisputeResolved(
        uint256 indexed disputeId,
        uint256 indexed projectId,
        ResolutionType resolution
    );

    /**
     * @dev Emitted when evidence is submitted for a dispute
     */
    event EvidenceSubmitted(
        uint256 indexed disputeId,
        address submitter,
        string evidence
    );

    /**
     * @dev Emitted when a response is submitted for a dispute
     */
    event ResponseSubmitted(
        uint256 indexed disputeId,
        address responder,
        string response
    );

    /**
     * @dev Creates a new dispute
     */
    function createDispute(
        uint256 projectId,
        uint256 milestoneId,
        address respondent,
        string calldata reason
    ) external returns (uint256);

    /**
     * @dev Submits evidence for a dispute
     */
    function submitEvidence(uint256 disputeId, string calldata evidence) external;

    /**
     * @dev Submits a response to a dispute
     */
    function submitResponse(uint256 disputeId, string calldata response) external;

    /**
     * @dev Resolves a dispute
     */
    function resolveDispute(
        uint256 disputeId,
        ResolutionType resolution
    ) external;

    /**
     * @dev Cancels a dispute
     */
    function cancelDispute(uint256 disputeId) external;

    /**
     * @dev Gets a dispute by ID
     */
    function getDispute(uint256 disputeId) external view returns (Dispute memory);

    /**
     * @dev Gets all disputes for a project
     */
    function getProjectDisputes(uint256 projectId) external view returns (uint256[] memory);

    /**
     * @dev Gets all disputes initiated by a user
     */
    function getUserInitiatedDisputes(address user) external view returns (uint256[] memory);

    /**
     * @dev Gets all disputes involving a user (either as initiator or respondent)
     */
    function getUserDisputes(address user) external view returns (uint256[] memory);

    /**
     * @dev Checks if a dispute exists for a milestone
     */
    function hasDispute(uint256 projectId, uint256 milestoneId) external view returns (bool);
}

contracts/interfaces/IEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IEscrow
 * @dev Interface for the Escrow contract that handles secure payments
 */
interface IEscrow {
    /**
     * @dev Enum representing the status of an escrow payment
     */
    enum EscrowStatus {
        Funded,
        Released,
        Refunded,
        Disputed
    }

    /**
     * @dev Struct representing an escrow payment
     */
    struct EscrowPayment {
        uint256 id;
        uint256 projectId;
        uint256 milestoneId;
        address client;
        address freelancer;
        uint256 amount;
        EscrowStatus status;
        uint256 createdAt;
    }

    /**
     * @dev Emitted when a new escrow payment is created
     */
    event EscrowCreated(
        uint256 indexed escrowId,
        uint256 indexed projectId,
        uint256 indexed milestoneId,
        address client,
        address freelancer,
        uint256 amount
    );

    /**
     * @dev Emitted when an escrow payment is released to the freelancer
     */
    event EscrowReleased(
        uint256 indexed escrowId,
        uint256 indexed projectId,
        uint256 indexed milestoneId,
        address freelancer,
        uint256 amount
    );

    /**
     * @dev Emitted when an escrow payment is refunded to the client
     */
    event EscrowRefunded(
        uint256 indexed escrowId,
        uint256 indexed projectId,
        uint256 indexed milestoneId,
        address client,
        uint256 amount
    );

    /**
     * @dev Emitted when an escrow payment is disputed
     */
    event EscrowDisputed(
        uint256 indexed escrowId,
        uint256 indexed projectId,
        uint256 indexed milestoneId,
        address initiator
    );

    /**
     * @dev Creates a new escrow payment
     */
    function createEscrow(
        uint256 projectId,
        uint256 milestoneId,
        address client,
        address freelancer
    ) external payable returns (uint256);

    /**
     * @dev Releases the escrow payment to the freelancer
     */
    function releaseEscrow(uint256 escrowId) external;

    /**
     * @dev Refunds the escrow payment to the client
     */
    function refundEscrow(uint256 escrowId) external;

    /**
     * @dev Disputes an escrow payment
     */
    function disputeEscrow(uint256 escrowId) external;

    /**
     * @dev Resolves a disputed escrow payment
     */
    function resolveDispute(uint256 escrowId, address payable recipient, uint256 amount) external;

    /**
     * @dev Gets an escrow payment by ID
     */
    function getEscrow(uint256 escrowId) external view returns (EscrowPayment memory);

    /**
     * @dev Gets all escrow payments for a project
     */
    function getProjectEscrows(uint256 projectId) external view returns (uint256[] memory);

    /**
     * @dev Gets all escrow payments for a milestone
     */
    function getMilestoneEscrow(uint256 projectId, uint256 milestoneId) external view returns (uint256);

    /**
     * @dev Gets the total amount held in escrow for a project
     */
    function getProjectEscrowBalance(uint256 projectId) external view returns (uint256);
}

contracts/interfaces/IFreelanceMarketpla.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IFreelanceMarketplace
 * @dev Interface for the Freelance Marketplace contract
 */
interface IFreelanceMarketplace {
    /**
     * @dev Enum representing the status of a project
     */
    enum ProjectStatus {
        Open,
        InProgress,
        Completed,
        Cancelled,
        Disputed
    }

    /**
     * @dev Enum representing the status of a milestone
     */
    enum MilestoneStatus {
        Pending,
        InProgress,
        Completed,
        Disputed
    }

    /**
     * @dev Struct representing a project
     */
    struct Project {
        uint256 id;
        address client;
        address freelancer;
        string title;
        string description;
        uint256 totalBudget;
        uint256 createdAt;
        uint256 deadline;
        ProjectStatus status;
        uint256[] milestoneIds;
    }

    /**
     * @dev Struct representing a milestone in a project
     */
    struct Milestone {
        uint256 id;
        uint256 projectId;
        string description;
        uint256 amount;
        uint256 deadline;
        MilestoneStatus status;
    }

    /**
     * @dev Struct representing a proposal for a project
     */
    struct Proposal {
        uint256 id;
        uint256 projectId;
        address freelancer;
        string description;
        uint256 estimatedCompletionTime;
        uint256 proposedBudget;
        bool accepted;
    }

    /**
     * @dev Emitted when a new project is created
     */
    event ProjectCreated(uint256 indexed projectId, address indexed client, string title, uint256 totalBudget);

    /**
     * @dev Emitted when a project's status is updated
     */
    event ProjectStatusUpdated(uint256 indexed projectId, ProjectStatus status);

    /**
     * @dev Emitted when a new milestone is added to a project
     */
    event MilestoneCreated(uint256 indexed projectId, uint256 indexed milestoneId, uint256 amount);

    /**
     * @dev Emitted when a milestone's status is updated
     */
    event MilestoneStatusUpdated(uint256 indexed milestoneId, MilestoneStatus status);

    /**
     * @dev Emitted when a new proposal is submitted
     */
    event ProposalSubmitted(uint256 indexed projectId, uint256 indexed proposalId, address indexed freelancer);

    /**
     * @dev Emitted when a proposal is accepted
     */
    event ProposalAccepted(uint256 indexed projectId, uint256 indexed proposalId, address indexed freelancer);

    /**
     * @dev Emitted when funds are released for a milestone
     */
    event MilestoneFundsReleased(uint256 indexed projectId, uint256 indexed milestoneId, address freelancer, uint256 amount);

    /**
     * @dev Emitted when a dispute is created
     */
    event DisputeCreated(uint256 indexed projectId, address initiator);

    /**
     * @dev Creates a new project
     */
    function createProject(
        string calldata title,
        string calldata description,
        uint256 totalBudget,
        uint256 deadline
    ) external payable returns (uint256);

    /**
     * @dev Adds a milestone to a project
     */
    function addMilestone(
        uint256 projectId,
        string calldata description,
        uint256 amount,
        uint256 deadline
    ) external returns (uint256);

    /**
     * @dev Submits a proposal for a project
     */
    function submitProposal(
        uint256 projectId,
        string calldata description,
        uint256 estimatedCompletionTime,
        uint256 proposedBudget
    ) external returns (uint256);

    /**
     * @dev Accepts a proposal for a project
     */
    function acceptProposal(uint256 projectId, uint256 proposalId) external;

    /**
     * @dev Starts a milestone
     */
    function startMilestone(uint256 projectId, uint256 milestoneId) external;

    /**
     * @dev Completes a milestone
     */
    function completeMilestone(uint256 projectId, uint256 milestoneId) external;

    /**
     * @dev Approves a completed milestone and releases funds
     */
    function approveMilestone(uint256 projectId, uint256 milestoneId) external;

    /**
     * @dev Creates a dispute for a project
     */
    function createDispute(uint256 projectId, string calldata reason) external;

    /**
     * @dev Gets a project by ID
     */
    function getProject(uint256 projectId) external view returns (Project memory);

    /**
     * @dev Gets a milestone by ID
     */
    function getMilestone(uint256 milestoneId) external view returns (Milestone memory);

    /**
     * @dev Gets a proposal by ID
     */
    function getProposal(uint256 proposalId) external view returns (Proposal memory);

    /**
     * @dev Gets all projects created by a client
     */
    function getClientProjects(address client) external view returns (uint256[] memory);

    /**
     * @dev Gets all projects assigned to a freelancer
     */
    function getFreelancerProjects(address freelancer) external view returns (uint256[] memory);

    /**
     * @dev Gets all proposals for a project
     */
    function getProjectProposals(uint256 projectId) external view returns (uint256[] memory);
}

contracts/interfaces/IReputationSystem.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IReputationSystem
 * @dev Interface for the Reputation System contract
 */
interface IReputationSystem {
    /**
     * @dev Struct representing a review
     */
    struct Review {
        uint256 id;
        uint256 projectId;
        address reviewer;
        address reviewee;
        uint8 rating; // 1-5 stars
        string comment;
        uint256 timestamp;
    }

    /**
     * @dev Struct representing a user's reputation
     */
    struct Reputation {
        uint256 totalRating;
        uint256 reviewCount;
        uint256[] reviewIds;
    }

    /**
     * @dev Emitted when a new review is submitted
     */
    event ReviewSubmitted(
        uint256 indexed reviewId,
        uint256 indexed projectId,
        address indexed reviewer,
        address reviewee,
        uint8 rating
    );

    /**
     * @dev Submits a review for a freelancer or client
     */
    function submitReview(
        uint256 projectId,
        address reviewee,
        uint8 rating,
        string calldata comment
    ) external returns (uint256);

    /**
     * @dev Gets a review by ID
     */
    function getReview(uint256 reviewId) external view returns (Review memory);

    /**
     * @dev Gets all reviews for a user
     */
    function getUserReviews(address user) external view returns (uint256[] memory);

    /**
     * @dev Gets a user's average rating
     */
    function getUserRating(address user) external view returns (uint256 rating, uint256 reviewCount);

    /**
     * @dev Gets all reviews for a project
     */
    function getProjectReviews(uint256 projectId) external view returns (uint256[] memory);

    /**
     * @dev Checks if a user can submit a review for a project
     */
    function canReview(address reviewer, uint256 projectId, address reviewee) external view returns (bool);

    /**
     * @dev Gets the reputation details for a user
     */
    function getReputation(address user) external view returns (Reputation memory);
}

